```{.r .cell-code}
# Base 2x2 design with condition means
design_tbl <- tribble(
  ~Condition, ~`Coffee`, ~`No Coffee`,
  "TV",       3,          4,
  "No TV",    4,          8
)

# Compute marginal means
design_tbl_extended <- design_tbl |>
  mutate(Row_Mean = rowMeans(across(c(`Coffee`, `No Coffee`)))) |>
  bind_rows(
    tibble(
      Condition = "Column Mean",
