```{.r .cell-code}
set.seed(2025)

n <- 80

# Attitude ratings (1–10 scale)
beginning <- rnorm(n, mean = 4.5, sd = 1.4)   # before any modules
mid       <- beginning + rnorm(n, mean = 1.0, sd = 0.6)  # after 4 modules
end       <- mid + rnorm(n, mean = 0.8, sd = 0.5)        # after 8 modules

# Bound values between 1 and 10
clip <- function(x) pmin(pmax(x, 1), 10)

beginning <- clip(beginning)
mid       <- clip(mid)
end       <- clip(end)

# Create long dataset
data_long2 <- tibble(
  id = factor(1:n),
  beginning = beginning,
  mid = mid,
  end = end
) |>
  pivot_longer(
    cols = beginning:end,
    names_to = "time",
    values_to = "attitude"
  ) |> 
  mutate(
    time = factor(time, 
                  levels = c("beginning","mid","end"))
    )

# ------------------------------
# Repeated-Measures ANOVA
# ------------------------------

rm_aov <- aov_ez(
  id = "id",
  dv = "attitude",
  within = "time",
  data = data_long2,
  anova_table = list(es = "ges"),
  return = "aov"
)

# Clean table for display
rm_aov |>
  tidy() |>
  rename(
    Source = term,
    SS = sumsq,
    MS = meansq,
    `F` = statistic, 
    p = p.value
  ) |> 
  select(!stratum) |>
  gt()
```

