)

clinical_data_long <- 
  clinical_data |> 
  tidyr::pivot_longer(
    cols = !Participant,
    names_to = "Dosage",
    values_to = "DV"
  )

aov(DV ~ Dosage + Error(Participant), data = clinical_data_long) |> 
  tidy() |> 
  gt()
