  tibble(
  grp = rep(c("SSLD","Placebo","Control"),each = 100),
  IQ = c(rnorm(100,140,5),
         rnorm(100,120,10),
         rnorm(100,100,15)) |&gt; round(0)
  )

exp |&gt; 
  group_by(grp) |&gt; 
  mutate(
     id = row_number()
